import React, { createContext, useState, useContext } from 'react';
import { useNavigate } from 'react-router-dom';
import toast from 'react-hot-toast';

const AuthContext = createContext({});

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  const demoUsers = {
    'donor@demo.com': { id: '1', name: 'Demo Donor', role: 'donor', bloodGroup: 'O+', isAvailable: true },
    'bloodbank@demo.com': { id: '2', name: 'City Blood Bank', role: 'blood-bank' },
    'ngo@demo.com': { id: '3', name: 'Red Cross NGO', role: 'ngo' },
    'admin@demo.com': { id: '4', name: 'System Admin', role: 'admin' }
  };

  const login = async (email, password) => {
    // Demo authentication
    if (demoUsers[email] && password === 'password') {
      const user = demoUsers[email];
      setUser(user);
      localStorage.setItem('user', JSON.stringify(user));
      toast.success(`Welcome back, ${user.name}!`);
      
      // Redirect based on role
      navigate(`/dashboard/${user.role}`);
      return { success: true };
    } else {
      toast.error('Invalid credentials. Use demo emails: donor@demo.com, bloodbank@demo.com, etc.');
      return { success: false };
    }
  };

  const register = async (userData) => {
    // Demo registration
    const newUser = {
      id: Date.now().toString(),
      name: userData.name,
      email: userData.email,
      role: userData.role || 'donor',
      bloodGroup: userData.bloodGroup || ''
    };
    
    setUser(newUser);
    localStorage.setItem('user', JSON.stringify(newUser));
    toast.success('Registration successful!');
    
    navigate(`/dashboard/${newUser.role}`);
    return { success: true };
  };

  const logout = () => {
    setUser(null);
    localStorage.removeItem('user');
    navigate('/login');
    toast.success('Logged out successfully');
  };

  const updateAvailability = (isAvailable) => {
    if (user) {
      const updatedUser = { ...user, isAvailable };
      setUser(updatedUser);
      localStorage.setItem('user', JSON.stringify(updatedUser));
      toast.success(`Availability updated to ${isAvailable ? 'Available' : 'Not Available'}`);
    }
  };

  // Check localStorage on mount
  React.useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
  }, []);

  const value = {
    user,
    login,
    register,
    logout,
    updateAvailability
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
