import React, { createContext, useState, useContext, useEffect } from 'react';
import toast from 'react-hot-toast';

const AuthContext = createContext({});

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [navigate, setNavigate] = useState(null);

  // Demo users data
  const demoUsers = {
    'donor@demo.com': { 
      id: '1', 
      name: 'Demo Donor', 
      role: 'donor', 
      bloodGroup: 'O+', 
      isAvailable: true,
      location: { city: 'New York', state: 'NY' }
    },
    'bloodbank@demo.com': { 
      id: '2', 
      name: 'City Blood Bank', 
      role: 'blood-bank',
      location: { city: 'New York', state: 'NY' }
    },
    'ngo@demo.com': { 
      id: '3', 
      name: 'Red Cross NGO', 
      role: 'ngo',
      location: { city: 'New York', state: 'NY' }
    },
    'admin@demo.com': { 
      id: '4', 
      name: 'System Admin', 
      role: 'admin',
      location: { city: 'New York', state: 'NY' }
    }
  };

  // Check localStorage on mount
  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }
  }, []);

  const login = async (email, password, navigateFunc) => {
    // Demo authentication
    if (demoUsers[email] && password === 'password') {
      const user = demoUsers[email];
      setUser(user);
      localStorage.setItem('user', JSON.stringify(user));
      toast.success(`Welcome back, ${user.name}!`);
      
      // Redirect based on role if navigate function is provided
      if (navigateFunc) {
        navigateFunc(`/dashboard/${user.role}`);
      }
      return { success: true, user };
    } else {
      toast.error('Invalid credentials. Use demo emails: donor@demo.com, bloodbank@demo.com, etc.');
      return { success: false };
    }
  };

  const register = async (userData, navigateFunc) => {
    // Demo registration
    const newUser = {
      id: Date.now().toString(),
      name: userData.name,
      email: userData.email,
      role: userData.role || 'donor',
      bloodGroup: userData.bloodGroup || '',
      location: userData.location || { city: '', state: '' },
      isAvailable: true
    };
    
    setUser(newUser);
    localStorage.setItem('user', JSON.stringify(newUser));
    toast.success('Registration successful!');
    
    // Redirect if navigate function is provided
    if (navigateFunc) {
      navigateFunc(`/dashboard/${newUser.role}`);
    }
    return { success: true, user: newUser };
  };

  const logout = (navigateFunc) => {
    setUser(null);
    localStorage.removeItem('user');
    toast.success('Logged out successfully');
    
    // Redirect to login if navigate function is provided
    if (navigateFunc) {
      navigateFunc('/login');
    }
  };

  const updateAvailability = (isAvailable) => {
    if (user) {
      const updatedUser = { ...user, isAvailable };
      setUser(updatedUser);
      localStorage.setItem('user', JSON.stringify(updatedUser));
      toast.success(`Availability updated to ${isAvailable ? 'Available' : 'Not Available'}`);
      return updatedUser;
    }
    return user;
  };

  const value = {
    user,
    login,
    register,
    logout,
    updateAvailability
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
