import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { FiUsers, FiDroplet, FiCalendar, FiBarChart2 } from 'react-icons/fi';

const AdminDashboard = () => {
  const [users, setUsers] = useState([]);
  const [requests, setRequests] = useState([]);
  const [camps, setCamps] = useState([]);
  const [stats, setStats] = useState({
    totalUsers: 0,
    totalDonors: 0,
    totalRequests: 0,
    totalCamps: 0
  });

  useEffect(() => {
    fetchAllData();
  }, []);

  const fetchAllData = async () => {
    try {
      const [usersRes, requestsRes, campsRes] = await Promise.all([
        axios.get('/api/users'),
        axios.get('/api/requests'),
        axios.get('/api/camps')
      ]);

      setUsers(usersRes.data.data);
      setRequests(requestsRes.data.data);
      setCamps(campsRes.data.data);

      const donors = usersRes.data.data.filter(u => u.role === 'donor').length;
      
      setStats({
        totalUsers: usersRes.data.data.length,
        totalDonors: donors,
        totalRequests: requestsRes.data.data.length,
        totalCamps: campsRes.data.data.length
      });
    } catch (error) {
      console.error('Failed to fetch data:', error);
    }
  };

  const getRoleBadgeColor = (role) => {
    switch(role) {
      case 'donor': return 'bg-red-100 text-red-800';
      case 'blood-bank': return 'bg-blue-100 text-blue-800';
      case 'ngo': return 'bg-green-100 text-green-800';
      case 'admin': return 'bg-purple-100 text-purple-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  return (
    <div className="max-w-7xl mx-auto">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">Admin Dashboard</h1>
        <p className="text-gray-600 mt-2">Monitor and manage the LifeLink platform</p>
      </div>

      {/* Stats */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6 mb-8">
        <div className="card">
          <div className="flex items-center">
            <div className="p-3 bg-red-100 rounded-lg mr-4">
              <FiUsers className="h-6 w-6 text-red-600" />
            </div>
            <div>
              <p className="text-sm text-gray-600">Total Users</p>
              <p className="text-2xl font-bold">{stats.totalUsers}</p>
            </div>
          </div>
        </div>

        <div className="card">
          <div className="flex items-center">
            <div className="p-3 bg-blue-100 rounded-lg mr-4">
              <FiDroplet className="h-6 w-6 text-blue-600" />
            </div>
            <div>
              <p className="text-sm text-gray-600">Total Donors</p>
              <p className="text-2xl font-bold">{stats.totalDonors}</p>
            </div>
          </div>
        </div>

        <div className="card">
          <div className="flex items-center">
            <div className="p-3 bg-green-100 rounded-lg mr-4">
              <FiBarChart2 className="h-6 w-6 text-green-600" />
            </div>
            <div>
              <p className="text-sm text-gray-600">Blood Requests</p>
              <p className="text-2xl font-bold">{stats.totalRequests}</p>
            </div>
          </div>
        </div>

        <div className="card">
          <div className="flex items-center">
            <div className="p-3 bg-purple-100 rounded-lg mr-4">
              <FiCalendar className="h-6 w-6 text-purple-600" />
            </div>
            <div>
              <p className="text-sm text-gray-600">Donation Camps</p>
              <p className="text-2xl font-bold">{stats.totalCamps}</p>
            </div>
          </div>
        </div>
      </div>

      {/* Users Table */}
      <div className="card mb-8">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-xl font-semibold">System Users</h2>
          <button
            onClick={fetchAllData}
            className="text-sm text-red-600 hover:text-red-700"
          >
            Refresh
          </button>
        </div>

        <div className="overflow-x-auto">
          <table className="min-w-full divide-y divide-gray-200">
            <thead>
              <tr>
                <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase">Name</th>
                <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase">Email</th>
                <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase">Role</th>
                <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase">Blood Group</th>
                <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase">Location</th>
                <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase">Joined</th>
              </tr>
            </thead>
            <tbody className="divide-y divide-gray-200">
              {users.map((user) => (
                <tr key={user._id} className="hover:bg-gray-50">
                  <td className="px-4 py-3">
                    <div className="font-medium">{user.name}</div>
                  </td>
                  <td className="px-4 py-3">
                    <div className="text-gray-600">{user.email}</div>
                  </td>
                  <td className="px-4 py-3">
                    <span className={`px-2 py-1 rounded-full text-xs ${getRoleBadgeColor(user.role)}`}>
                      {user.role}
                    </span>
                  </td>
                  <td className="px-4 py-3">
                    <span className="font-medium">{user.bloodGroup || '-'}</span>
                  </td>
                  <td className="px-4 py-3">
                    <span className="text-sm">
                      {user.location?.city || 'Not specified'}
                    </span>
                  </td>
                  <td className="px-4 py-3">
                    <span className="text-sm text-gray-500">
                      {new Date(user.createdAt).toLocaleDateString()}
                    </span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {/* Recent Requests & Camps */}
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-8">
        {/* Recent Requests */}
        <div className="card">
          <h2 className="text-xl font-semibold mb-6">Recent Blood Requests</h2>
          <div className="space-y-4">
            {requests.slice(0, 5).map((request) => (
              <div key={request._id} className="border-b pb-4 last:border-0 last:pb-0">
                <div className="flex justify-between items-start">
                  <div>
                    <h3 className="font-medium">{request.patientName}</h3>
                    <p className="text-sm text-gray-600">{request.hospital}</p>
                  </div>
                  <div className="text-right">
                    <span className="font-bold">{request.bloodGroup}</span>
                    <p className="text-xs text-gray-500">
                      {new Date(request.createdAt).toLocaleDateString()}
                    </p>
                  </div>
                </div>
                <div className="mt-2 flex items-center space-x-4">
                  <span className={`px-2 py-1 rounded text-xs ${
                    request.status === 'open' ? 'bg-green-100 text-green-800' :
                    request.status === 'assigned' ? 'bg-blue-100 text-blue-800' :
                    'bg-gray-100 text-gray-800'
                  }`}>
                    {request.status}
                  </span>
                  <span className="text-sm text-gray-600">
                    {request.units} units
                  </span>
                </div>
              </div>
            ))}
          </div>
        </div>

        {/* Recent Camps */}
        <div className="card">
          <h2 className="text-xl font-semibold mb-6">Recent Donation Camps</h2>
          <div className="space-y-4">
            {camps.slice(0, 5).map((camp) => (
              <div key={camp._id} className="border-b pb-4 last:border-0 last:pb-0">
                <div className="flex justify-between items-start">
                  <div>
                    <h3 className="font-medium">{camp.title}</h3>
                    <p className="text-sm text-gray-600">{camp.location}</p>
                  </div>
                  <span className={`px-2 py-1 rounded text-xs ${
                    camp.status === 'planned' ? 'bg-blue-100 text-blue-800' :
                    camp.status === 'ongoing' ? 'bg-green-100 text-green-800' :
                    'bg-gray-100 text-gray-800'
                  }`}>
                    {camp.status}
                  </span>
                </div>
                <div className="mt-2 flex items-center space-x-4">
                  <span className="text-sm text-gray-600">
                    {new Date(camp.date).toLocaleDateString()}
                  </span>
                  {camp.expectedDonors && (
                    <span className="text-sm text-gray-600">
                      {camp.expectedDonors} expected donors
                    </span>
                  )}
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
