import React from 'react';
import { Link } from 'react-router-dom';
import { FiDroplet, FiUsers, FiMapPin, FiCheckCircle } from 'react-icons/fi';

const Home = () => {
  const features = [
    {
      icon: <FiDroplet className="h-12 w-12" />,
      title: "Quick Blood Matching",
      description: "Find donors with matching blood groups instantly"
    },
    {
      icon: <FiUsers className="h-12 w-12" />,
      title: "Multi-role Platform",
      description: "Donors, blood banks, NGOs, and admins in one system"
    },
    {
      icon: <FiMapPin className="h-12 w-12" />,
      title: "Location-based Search",
      description: "Find nearby donors and blood requests"
    },
    {
      icon: <FiCheckCircle className="h-12 w-12" />,
      title: "Simple & Efficient",
      description: "Clean interface for emergency blood needs"
    }
  ];

  const roles = [
    {
      name: "Donor",
      description: "Register, update availability, and help save lives",
      color: "bg-red-100 text-red-800"
    },
    {
      name: "Blood Bank",
      description: "Manage inventory and fulfill blood requests",
      color: "bg-blue-100 text-blue-800"
    },
    {
      name: "NGO",
      description: "Organize blood donation camps and drives",
      color: "bg-green-100 text-green-800"
    },
    {
      name: "Admin",
      description: "Monitor system activity and manage users",
      color: "bg-purple-100 text-purple-800"
    }
  ];

  return (
    <div>
      {/* Hero Section */}
      <div className="relative bg-gradient-to-r from-red-600 to-red-700 text-white">
        <div className="max-w-7xl mx-auto px-4 py-24 sm:px-6 lg:px-8">
          <div className="text-center">
            <h1 className="text-4xl md:text-6xl font-bold mb-6">
              LifeLink
              <span className="block text-2xl md:text-3xl font-normal mt-2">
                Smart Blood Donation Platform
              </span>
            </h1>
            <p className="text-xl mb-8 max-w-3xl mx-auto">
              Connecting donors with recipients in real-time. A demo platform showcasing how technology can save lives.
            </p>
            <div className="space-x-4">
              <Link to="/register" className="btn-primary bg-white text-red-600 hover:bg-gray-100 px-8 py-3 text-lg">
                Get Started
              </Link>
              <Link to="/login" className="btn-secondary bg-transparent border-2 border-white hover:bg-white/10 px-8 py-3 text-lg">
                Login
              </Link>
            </div>
            <p className="mt-6 text-red-100">
              <span className="font-bold">DEMO VERSION</span> - Simplified for presentation
            </p>
          </div>
        </div>
      </div>

      {/* Features */}
      <div className="max-w-7xl mx-auto px-4 py-16 sm:px-6 lg:px-8">
        <h2 className="text-3xl font-bold text-center mb-12">How It Works</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-8">
          {features.map((feature, index) => (
            <div key={index} className="card text-center">
              <div className="text-red-600 mb-4 flex justify-center">
                {feature.icon}
              </div>
              <h3 className="text-xl font-semibold mb-2">{feature.title}</h3>
              <p className="text-gray-600">{feature.description}</p>
            </div>
          ))}
        </div>
      </div>

      {/* Roles */}
      <div className="bg-gray-50 py-16">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <h2 className="text-3xl font-bold text-center mb-12">Platform Roles</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
            {roles.map((role, index) => (
              <div key={index} className="card">
                <span className={`inline-block px-3 py-1 rounded-full text-sm font-medium mb-4 ${role.color}`}>
                  {role.name}
                </span>
                <p className="text-gray-600">{role.description}</p>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Demo Notice */}
      <div className="max-w-7xl mx-auto px-4 py-16 sm:px-6 lg:px-8">
        <div className="bg-yellow-50 border-l-4 border-yellow-400 p-6">
          <div className="flex">
            <div className="flex-shrink-0">
              <svg className="h-5 w-5 text-yellow-400" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M8.257 3.099c.765-1.36 2.722-1.36 3.486 0l5.58 9.92c.75 1.334-.213 2.98-1.742 2.98H4.42c-1.53 0-2.493-1.646-1.743-2.98l5.58-9.92zM11 13a1 1 0 11-2 0 1 1 0 012 0zm-1-8a1 1 0 00-1 1v3a1 1 0 002 0V6a1 1 0 00-1-1z" clipRule="evenodd" />
              </svg>
            </div>
            <div className="ml-3">
              <h3 className="text-lg font-medium text-yellow-800">Demo Version Notice</h3>
              <div className="mt-2 text-yellow-700">
                <p>
                  This is a demonstration version of LifeLink. Key features are simplified for presentation purposes.
                  Real-world implementation would include additional security, verification, and advanced features.
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Home;
