import React, { createContext, useState, useContext, useEffect } from 'react';
import toast from 'react-hot-toast';

const AuthContext = createContext({});

export const useAuth = () => useContext(AuthContext);

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [notifications, setNotifications] = useState([
    { id: 1, type: 'success', message: 'System initialized successfully', time: '2 min ago' },
    { id: 2, type: 'info', message: 'New blood request from John Smith', time: '5 min ago' },
    { id: 3, type: 'warning', message: 'Blood group O- running low', time: '10 min ago' }
  ]);
  const [systemStatus, setSystemStatus] = useState({
    backend: 'online',
    database: 'connected',
    users: 4,
    requests: 12
  });

  // Enhanced demo users with more data
  const demoUsers = {
    'donor@demo.com': { 
      id: '1', 
      name: 'Alex Johnson', 
      role: 'donor', 
      bloodGroup: 'O+', 
      isAvailable: true,
      location: { city: 'New York', state: 'NY' },
      totalDonations: 5,
      lastDonation: '2024-01-10',
      phone: '+1 (555) 123-4567'
    },
    'bloodbank@demo.com': { 
      id: '2', 
      name: 'City Blood Bank', 
      role: 'blood-bank',
      location: { city: 'New York', state: 'NY' },
      phone: '+1 (555) 987-6543',
      license: 'BB-2024-001',
      rating: 4.8
    },
    'ngo@demo.com': { 
      id: '3', 
      name: 'LifeSave NGO', 
      role: 'ngo',
      location: { city: 'New York', state: 'NY' },
      phone: '+1 (555) 456-7890',
      registration: 'NGO-2024-001',
      totalCamps: 15
    },
    'admin@demo.com': { 
      id: '4', 
      name: 'System Administrator', 
      role: 'admin',
      location: { city: 'New York', state: 'NY' },
      permissions: ['all'],
      lastLogin: new Date().toISOString()
    }
  };

  // Check localStorage on mount
  useEffect(() => {
    const storedUser = localStorage.getItem('user');
    if (storedUser) {
      setUser(JSON.parse(storedUser));
    }

    // Simulate real-time updates
    const interval = setInterval(() => {
      // Add random notification
      const newNotification = {
        id: Date.now(),
        type: ['info', 'success', 'warning'][Math.floor(Math.random() * 3)],
        message: ['New donor registered', 'Blood request completed', 'Inventory updated', 'Camp created'][Math.floor(Math.random() * 4)],
        time: 'Just now'
      };
      setNotifications(prev => [newNotification, ...prev.slice(0, 4)]);
    }, 30000); // Every 30 seconds

    return () => clearInterval(interval);
  }, []);

  const login = async (email, password, navigateFunc) => {
    // Simulate API call delay
    await new Promise(resolve => setTimeout(resolve, 800));
    
    if (demoUsers[email] && password === 'password') {
      const user = demoUsers[email];
      setUser(user);
      localStorage.setItem('user', JSON.stringify(user));
      
      // Add login notification
      const loginNotif = {
        id: Date.now(),
        type: 'success',
        message: `Welcome back, ${user.name}!`,
        time: 'Just now'
      };
      setNotifications(prev => [loginNotif, ...prev]);
      
      toast.success(`Welcome back, ${user.name}!`);
      
      // Redirect based on role
      if (navigateFunc) {
        navigateFunc(`/dashboard/${user.role}`);
      }
      return { success: true, user };
    } else {
      toast.error('Invalid credentials. Use demo emails: donor@demo.com, bloodbank@demo.com, etc.');
      return { success: false };
    }
  };

  const register = async (userData, navigateFunc) => {
    // Simulate API call
    await new Promise(resolve => setTimeout(resolve, 1000));
    
    const newUser = {
      id: Date.now().toString(),
      name: userData.name,
      email: userData.email,
      role: userData.role || 'donor',
      bloodGroup: userData.bloodGroup || '',
      location: userData.location || { city: '', state: '' },
      isAvailable: true,
      createdAt: new Date().toISOString()
    };
    
    setUser(newUser);
    localStorage.setItem('user', JSON.stringify(newUser));
    
    // Add registration notification
    const regNotif = {
      id: Date.now(),
      type: 'success',
      message: `New ${newUser.role} registered: ${newUser.name}`,
      time: 'Just now'
    };
    setNotifications(prev => [regNotif, ...prev]);
    
    toast.success('Registration successful! Welcome to LifeLink.');
    
    // Redirect
    if (navigateFunc) {
      navigateFunc(`/dashboard/${newUser.role}`);
    }
    return { success: true, user: newUser };
  };

  const logout = (navigateFunc) => {
    // Add logout notification
    const logoutNotif = {
      id: Date.now(),
      type: 'info',
      message: `${user?.name} logged out`,
      time: 'Just now'
    };
    setNotifications(prev => [logoutNotif, ...prev]);
    
    setUser(null);
    localStorage.removeItem('user');
    toast.success('Logged out successfully');
    
    if (navigateFunc) {
      navigateFunc('/login');
    }
  };

  const updateAvailability = (isAvailable) => {
    if (user) {
      const updatedUser = { ...user, isAvailable };
      setUser(updatedUser);
      localStorage.setItem('user', JSON.stringify(updatedUser));
      
      // Add notification
      const notif = {
        id: Date.now(),
        type: 'info',
        message: `Availability updated to ${isAvailable ? 'Available' : 'Not Available'}`,
        time: 'Just now'
      };
      setNotifications(prev => [notif, ...prev]);
      
      toast.success(`You are now ${isAvailable ? 'available' : 'unavailable'} for donations`);
      return updatedUser;
    }
    return user;
  };

  const addNotification = (notification) => {
    setNotifications(prev => [notification, ...prev]);
  };

  const clearNotifications = () => {
    setNotifications([]);
  };

  const value = {
    user,
    notifications,
    systemStatus,
    login,
    register,
    logout,
    updateAvailability,
    addNotification,
    clearNotifications
  };

  return (
    <AuthContext.Provider value={value}>
      {children}
    </AuthContext.Provider>
  );
};
