import React, { useState, useEffect } from 'react';
import axios from 'axios';
import toast from 'react-hot-toast';
import { FiDroplet, FiPackage, FiCheckCircle, FiPlus, FiMinus } from 'react-icons/fi';

const BloodBankDashboard = () => {
  const [inventory, setInventory] = useState(null);
  const [requests, setRequests] = useState([]);
  const [loading, setLoading] = useState(true);

  const bloodGroups = ['A+', 'A-', 'B+', 'B-', 'O+', 'O-', 'AB+', 'AB-'];

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const [inventoryRes, requestsRes] = await Promise.all([
        axios.get('/api/inventory'),
        axios.get('/api/requests')
      ]);
      setInventory(inventoryRes.data.data);
      setRequests(requestsRes.data.data);
    } catch (error) {
      toast.error('Failed to fetch data');
    } finally {
      setLoading(false);
    }
  };

  const updateInventory = async (bloodGroup, action) => {
    try {
      const response = await axios.put('/api/inventory', { bloodGroup, action });
      setInventory(response.data.data);
      toast.success('Inventory updated');
    } catch (error) {
      toast.error('Failed to update inventory');
    }
  };

  const acceptRequest = async (requestId) => {
    try {
      await axios.put(`/api/requests/${requestId}/accept`);
      toast.success('Request accepted successfully!');
      fetchData();
    } catch (error) {
      toast.error('Failed to accept request');
    }
  };

  if (loading) {
    return (
      <div className="flex items-center justify-center h-64">
        <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-red-600"></div>
      </div>
    );
  }

  return (
    <div className="max-w-7xl mx-auto">
      {/* Header */}
      <div className="mb-8">
        <h1 className="text-3xl font-bold text-gray-900">Blood Bank Dashboard</h1>
        <p className="text-gray-600 mt-2">Manage your blood inventory and fulfill requests</p>
      </div>

      {/* Inventory */}
      <div className="card mb-8">
        <h2 className="text-xl font-semibold mb-6">Blood Inventory Management</h2>
        
        <div className="grid grid-cols-2 md:grid-cols-4 gap-4">
          {bloodGroups.map((group) => (
            <div key={group} className="border rounded-lg p-4">
              <div className="flex justify-between items-center mb-4">
                <div>
                  <p className="text-sm text-gray-600">Blood Group</p>
                  <p className="text-2xl font-bold">{group}</p>
                </div>
                <div className="text-right">
                  <p className="text-3xl font-bold">{inventory?.bloodGroups[group] || 0}</p>
                  <p className="text-sm text-gray-600">units</p>
                </div>
              </div>
              
              <div className="flex space-x-2">
                <button
                  onClick={() => updateInventory(group, 'increase')}
                  className="flex-1 bg-green-100 text-green-700 hover:bg-green-200 py-2 rounded flex items-center justify-center"
                >
                  <FiPlus />
                </button>
                <button
                  onClick={() => updateInventory(group, 'decrease')}
                  disabled={!inventory?.bloodGroups[group]}
                  className="flex-1 bg-red-100 text-red-700 hover:bg-red-200 py-2 rounded flex items-center justify-center disabled:opacity-50"
                >
                  <FiMinus />
                </button>
              </div>
            </div>
          ))}
        </div>

        <div className="mt-6 pt-6 border-t">
          <div className="flex justify-between items-center">
            <div>
              <p className="text-sm text-gray-600">Total Blood Units</p>
              <p className="text-2xl font-bold">
                {Object.values(inventory?.bloodGroups || {}).reduce((a, b) => a + b, 0)}
              </p>
            </div>
            <div className="text-sm text-gray-600">
              Last updated: {new Date(inventory?.lastUpdated).toLocaleString()}
            </div>
          </div>
        </div>
      </div>

      {/* Blood Requests */}
      <div className="card">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-xl font-semibold">Blood Requests</h2>
          <button
            onClick={fetchData}
            className="text-sm text-red-600 hover:text-red-700"
          >
            Refresh
          </button>
        </div>

        {requests.length === 0 ? (
          <div className="text-center py-12">
            <FiDroplet className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            <p className="text-gray-600">No blood requests at the moment.</p>
          </div>
        ) : (
          <div className="overflow-x-auto">
            <table className="min-w-full divide-y divide-gray-200">
              <thead>
                <tr>
                  <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase">Patient</th>
                  <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase">Blood</th>
                  <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase">Units</th>
                  <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase">Urgency</th>
                  <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase">Location</th>
                  <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase">Status</th>
                  <th className="px-4 py-3 text-left text-xs font-medium text-gray-500 uppercase">Action</th>
                </tr>
              </thead>
              <tbody className="divide-y divide-gray-200">
                {requests.map((request) => (
                  <tr key={request._id} className="hover:bg-gray-50">
                    <td className="px-4 py-3">
                      <div>
                        <p className="font-medium">{request.patientName}</p>
                        <p className="text-sm text-gray-600">{request.hospital}</p>
                      </div>
                    </td>
                    <td className="px-4 py-3">
                      <span className="font-bold text-lg">{request.bloodGroup}</span>
                    </td>
                    <td className="px-4 py-3">
                      <span className="font-semibold">{request.units}</span>
                    </td>
                    <td className="px-4 py-3">
                      <span className={`px-2 py-1 rounded-full text-xs ${
                        request.urgency === 'critical' ? 'bg-red-100 text-red-800' :
                        request.urgency === 'high' ? 'bg-orange-100 text-orange-800' :
                        request.urgency === 'medium' ? 'bg-yellow-100 text-yellow-800' :
                        'bg-green-100 text-green-800'
                      }`}>
                        {request.urgency}
                      </span>
                    </td>
                    <td className="px-4 py-3">
                      <p className="text-sm">{request.location}</p>
                    </td>
                    <td className="px-4 py-3">
                      <span className={`px-2 py-1 rounded-full text-xs ${
                        request.status === 'open' ? 'bg-green-100 text-green-800' :
                        request.status === 'assigned' ? 'bg-blue-100 text-blue-800' :
                        'bg-gray-100 text-gray-800'
                      }`}>
                        {request.status}
                      </span>
                    </td>
                    <td className="px-4 py-3">
                      {request.status === 'open' ? (
                        <button
                          onClick={() => acceptRequest(request._id)}
                          className="btn-primary text-sm py-1 px-3"
                        >
                          Accept
                        </button>
                      ) : (
                        <span className="text-gray-500">Accepted</span>
                      )}
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        )}
      </div>
    </div>
  );
};

export default BloodBankDashboard;
