import React, { useState, useEffect } from 'react';
import axios from 'axios';
import toast from 'react-hot-toast';
import { FiCalendar, FiMapPin, FiUsers, FiPlus, FiUpload } from 'react-icons/fi';

const NGODashboard = () => {
  const [camps, setCamps] = useState([]);
  const [showCampForm, setShowCampForm] = useState(false);
  const [newCamp, setNewCamp] = useState({
    title: '',
    description: '',
    date: '',
    location: '',
    expectedDonors: ''
  });
  const [uploading, setUploading] = useState(false);

  useEffect(() => {
    fetchCamps();
  }, []);

  const fetchCamps = async () => {
    try {
      const response = await axios.get('/api/camps');
      setCamps(response.data.data);
    } catch (error) {
      toast.error('Failed to fetch camps');
    }
  };

  const handleCreateCamp = async (e) => {
    e.preventDefault();
    try {
      await axios.post('/api/camps', {
        ...newCamp,
        date: new Date(newCamp.date),
        expectedDonors: parseInt(newCamp.expectedDonors)
      });
      toast.success('Camp created successfully!');
      setShowCampForm(false);
      setNewCamp({
        title: '',
        description: '',
        date: '',
        location: '',
        expectedDonors: ''
      });
      fetchCamps();
    } catch (error) {
      toast.error('Failed to create camp');
    }
  };

  const handleFileUpload = async (e) => {
    const file = e.target.files[0];
    if (!file) return;

    setUploading(true);
    // Simulate upload
    setTimeout(() => {
      toast.success('Document uploaded successfully (demo)');
      setUploading(false);
    }, 1500);
  };

  const getStatusColor = (status) => {
    switch(status) {
      case 'planned': return 'bg-blue-100 text-blue-800';
      case 'ongoing': return 'bg-green-100 text-green-800';
      case 'completed': return 'bg-gray-100 text-gray-800';
      default: return 'bg-gray-100 text-gray-800';
    }
  };

  return (
    <div className="max-w-7xl mx-auto">
      {/* Header */}
      <div className="mb-8">
        <div className="flex justify-between items-center">
          <div>
            <h1 className="text-3xl font-bold text-gray-900">NGO Dashboard</h1>
            <p className="text-gray-600 mt-2">Organize and manage blood donation camps</p>
          </div>
          <div className="flex items-center space-x-4">
            <label className="btn-secondary cursor-pointer">
              <input
                type="file"
                className="hidden"
                onChange={handleFileUpload}
                disabled={uploading}
              />
              <div className="flex items-center space-x-2">
                <FiUpload />
                <span>{uploading ? 'Uploading...' : 'Upload Document'}</span>
              </div>
            </label>
            <button
              onClick={() => setShowCampForm(true)}
              className="btn-primary flex items-center space-x-2"
            >
              <FiPlus />
              <span>Create Camp</span>
            </button>
          </div>
        </div>
      </div>

      {/* Stats */}
      <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
        <div className="card">
          <div className="flex items-center">
            <div className="p-3 bg-red-100 rounded-lg mr-4">
              <FiCalendar className="h-6 w-6 text-red-600" />
            </div>
            <div>
              <p className="text-sm text-gray-600">Total Camps</p>
              <p className="text-2xl font-bold">{camps.length}</p>
            </div>
          </div>
        </div>

        <div className="card">
          <div className="flex items-center">
            <div className="p-3 bg-blue-100 rounded-lg mr-4">
              <FiUsers className="h-6 w-6 text-blue-600" />
            </div>
            <div>
              <p className="text-sm text-gray-600">Expected Donors</p>
              <p className="text-2xl font-bold">
                {camps.reduce((sum, camp) => sum + (camp.expectedDonors || 0), 0)}
              </p>
            </div>
          </div>
        </div>

        <div className="card">
          <div className="flex items-center">
            <div className="p-3 bg-green-100 rounded-lg mr-4">
              <FiMapPin className="h-6 w-6 text-green-600" />
            </div>
            <div>
              <p className="text-sm text-gray-600">Active Camps</p>
              <p className="text-2xl font-bold">
                {camps.filter(camp => camp.status === 'ongoing').length}
              </p>
            </div>
          </div>
        </div>
      </div>

      {/* Create Camp Modal */}
      {showCampForm && (
        <div className="fixed inset-0 bg-black bg-opacity-50 flex items-center justify-center p-4 z-50">
          <div className="bg-white rounded-lg max-w-2xl w-full max-h-[90vh] overflow-y-auto">
            <div className="p-6">
              <div className="flex justify-between items-center mb-6">
                <h2 className="text-2xl font-bold">Create Donation Camp</h2>
                <button
                  onClick={() => setShowCampForm(false)}
                  className="text-gray-400 hover:text-gray-600"
                >
                  âœ•
                </button>
              </div>

              <form onSubmit={handleCreateCamp} className="space-y-4">
                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Camp Title *
                  </label>
                  <input
                    type="text"
                    required
                    value={newCamp.title}
                    onChange={(e) => setNewCamp({...newCamp, title: e.target.value})}
                    className="input-field"
                    placeholder="Enter camp title"
                  />
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Description
                  </label>
                  <textarea
                    value={newCamp.description}
                    onChange={(e) => setNewCamp({...newCamp, description: e.target.value})}
                    className="input-field h-24"
                    placeholder="Describe the camp purpose..."
                  />
                </div>

                <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Date *
                    </label>
                    <input
                      type="date"
                      required
                      value={newCamp.date}
                      onChange={(e) => setNewCamp({...newCamp, date: e.target.value})}
                      className="input-field"
                    />
                  </div>

                  <div>
                    <label className="block text-sm font-medium text-gray-700 mb-1">
                      Expected Donors
                    </label>
                    <input
                      type="number"
                      min="1"
                      value={newCamp.expectedDonors}
                      onChange={(e) => setNewCamp({...newCamp, expectedDonors: e.target.value})}
                      className="input-field"
                      placeholder="e.g., 100"
                    />
                  </div>
                </div>

                <div>
                  <label className="block text-sm font-medium text-gray-700 mb-1">
                    Location *
                  </label>
                  <input
                    type="text"
                    required
                    value={newCamp.location}
                    onChange={(e) => setNewCamp({...newCamp, location: e.target.value})}
                    className="input-field"
                    placeholder="Enter camp location"
                  />
                </div>

                <div className="flex justify-end space-x-3 pt-4">
                  <button
                    type="button"
                    onClick={() => setShowCampForm(false)}
                    className="btn-secondary"
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    className="btn-primary"
                  >
                    Create Camp
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}

      {/* Camps List */}
      <div className="card">
        <div className="flex justify-between items-center mb-6">
          <h2 className="text-xl font-semibold">Your Donation Camps</h2>
          <button
            onClick={fetchCamps}
            className="text-sm text-red-600 hover:text-red-700"
          >
            Refresh
          </button>
        </div>

        {camps.length === 0 ? (
          <div className="text-center py-12">
            <FiCalendar className="h-12 w-12 text-gray-400 mx-auto mb-4" />
            <p className="text-gray-600">No camps created yet.</p>
            <button
              onClick={() => setShowCampForm(true)}
              className="btn-primary mt-4"
            >
              Create Your First Camp
            </button>
          </div>
        ) : (
          <div className="space-y-4">
            {camps.map((camp) => (
              <div key={camp._id} className="border rounded-lg p-6 hover:bg-gray-50">
                <div className="flex justify-between items-start">
                  <div className="flex-1">
                    <div className="flex items-center space-x-3 mb-2">
                      <h3 className="text-xl font-semibold">{camp.title}</h3>
                      <span className={`px-3 py-1 rounded-full text-sm ${getStatusColor(camp.status)}`}>
                        {camp.status}
                      </span>
                    </div>
                    
                    {camp.description && (
                      <p className="text-gray-600 mb-4">{camp.description}</p>
                    )}
                    
                    <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                      <div className="flex items-center space-x-2">
                        <FiCalendar className="text-gray-400" />
                        <div>
                          <p className="text-sm text-gray-600">Date</p>
                          <p className="font-medium">
                            {new Date(camp.date).toLocaleDateString()}
                          </p>
                        </div>
                      </div>
                      
                      <div className="flex items-center space-x-2">
                        <FiMapPin className="text-gray-400" />
                        <div>
                          <p className="text-sm text-gray-600">Location</p>
                          <p className="font-medium">{camp.location}</p>
                        </div>
                      </div>
                      
                      <div className="flex items-center space-x-2">
                        <FiUsers className="text-gray-400" />
                        <div>
                          <p className="text-sm text-gray-600">Expected Donors</p>
                          <p className="font-medium">{camp.expectedDonors || 'Not specified'}</p>
                        </div>
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default NGODashboard;
